import pygamefrom pygame.sprite import Spriteclass Bullet(Sprite):    """Класс для управления снарядами, выпущенными кораблем"""    def __init__(self, ai_game):        super().__init__()        self.settings = ai_game.settings        self.screen = ai_game.screen  # Используем screen из ai_game        self.color = self.settings.bullet_color        # Создание снаряда в позиции (0,0) и значение правильной позиции        self.rect = pygame.Rect(0, 0, self.settings.bullet_width,                                self.settings.bullet_height)        self.rect.midtop = ai_game.ship.rect.midtop        # Позиция снаряда хранится в вещественном формате.        self.y = float(self.rect.y)    def update(self):        """Перемещает снаряд вверх по экрану."""        self.y -= self.settings.bullet_speed        self.rect.y = int(self.y)    def draw_bullet(self):        """Выводит снаряд на экран."""        pygame.draw.rect(self.screen, self.color, self.rect)  # Используем self.screen