import pygamefrom pygame.sprite import Spriteclass Bullet(Sprite):    """Класс для управления пулями, выпущенными кораблем."""    def __init__(self, ai_game):        """Создает объект пули в текущей позиции корабля."""        super().__init__()        self.screen = ai_game.screen        self.settings = ai_game.settings        self.color = self.settings.bullet_color        # Создает прямоугольник пули на основе координат корабля        self.rect = pygame.Rect(0, 0, self.settings.bullet_width, self.settings.bullet_height)        self.rect.centerx = ai_game.ship.rect.centerx        self.rect.top = ai_game.ship.rect.top        # Сохраняет точную позицию пули        self.y = float(self.rect.y)    def update(self):        """Перемещает пулю вверх по экрану."""        self.y -= self.settings.bullet_speed        self.rect.y = self.y    def draw_bullet(self):        """Рисует пулю на экране."""        pygame.draw.rect(self.screen, self.color, self.rect)