class Settings:    """Хранит все настройки игры Alien Invasion."""    def __init__(self):        """Инициализирует настройки игры."""        # Настройки экрана        self.screen_width = 1200        self.screen_height = 800        self.bg_color = (230, 230, 230)        # Настройки корабля        self.ship_speed = 1.5        # Настройки пуль        self.bullet_speed = 1        self.bullet_width = 3        self.bullet_height = 15        self.bullet_color = (60, 60, 60)        self.bullets_allowed = 3  # Максимальное количество пуль на экране        # Настройки игры        self.speedup_scale = 1.1        self.initialize_dynamic_settings()    def initialize_dynamic_settings(self):        """Инициализирует настройки, изменяющиеся в ходе игры."""        self.ship_speed = 1.5        self.bullet_speed = 1